import React, { Component } from 'react';

import { connect } from 'react-redux'

import ReviewCard from '.component/'


class Show extends Component {


    componentDidMount(){

        fetch(`http://localhost:3000/reviews/${this.props.currentBusiness.id}`)
        .then(response => response.json())
        .then(data => {
        console.log('Success:', data);
        })
        .catch((error) => {
        console.error('Error:', error);
        });
    }



    render(){
return(
    <div>
    Show Component
    <h2> { this.props.currentBusiness.name } </h2>
    <h5> { this.props.currentBusiness.address } </h5>

    <h4> { this.props.currentBusiness.bio } </h4>
    <br/>
    <button> Write Review </button>

    <h4> Reviews </h4>

    <ReviewCard/>

    </div>
)
}
}

const mapStateToProps = state =>  {
    return { currentBusiness: state.currentBusiness  }
}

const mapDispatchToProps = dispatch => {
  return {
    loadBusinesses: (businesses) => dispatch({ type: "Load_Businesses", payload: businesses }),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Show);
