        console.log(e.target)
        if (e.target.checked == false || e.target.checked == null){
            e.target.checked = true
        } else {e.target.checked = false}
    }

render(){
return(
    <div className= "">
        <input type="checkbox" id="scales" onChange= {(e)=> this.handleCheck(e)} name={this.props.trait.name} defaultChecked={false} /> {this.props.trait.name}

    </div>
)
}
}
