import React from 'react';

import { useSelector, useDispatch } from 'react-redux'


const Show = () => {

    const {currentBusiness}  = useSelector( state => {
        return { currentBusiness: state.currentBusiness}
    })


    if (currentBusiness !== {}){
    fetchReviews(currentBusiness)
    }

    const fetchReviews = (id) => {

            fetch(`http://localhost:3000/reviews/${id}`)
          .then(response => response.json())
          .then(data => {
            console.log('Success:', data);
            this.props.loadBusinesses(data)
          })
          .catch((error) => {
            console.error('Error:', error);

          });
    }

    // console.log(currentBusiness)

return(
    <div>
    Show Component
    <h2> { currentBusiness.name } </h2>
    <h5> { currentBusiness.address } </h5>

    <h4> { currentBusiness.bio } </h4>
    <br/>
    <button> Write Review </button>

    <h4> Reviews </h4>

    </div>
)
}
export default Show
