        fetch(`http://localhost:3000/reviews/${this.props.currentBusiness.id}`)
        .then(response => response.json())
        .then(data => {
        console.log('Success:', data);
        this.props.loadReviews(data)
        })
        .catch((error) => {
        console.error('Error:', error);
        });
    }

